import React from "react";
import Weather from "./components/Weather";
import "./App.css";

function App() {
  const week = [
    { day: "Sunday", low: "33", high: "35" },
    { day: "Mon", low: "26", high: "34" },
    { day: "Tue", low: "26", high: "34" },
    { day: "Wednesday", low: "32", high: "38" },
    { day: "Thursday", low: "17", high: "21" },
    { day: "Friday", low: "12", high: "14" },
    { day: "Saturday", low: "-4", high: "8" },
  ];
  const renderWeek = function () {
    const array = [];
    for (let i = 0; i < week.length; i++) {
      const weather = (
        <Weather day={week[i].day} low={week[i].low} high={week[i].high} />
      );
      let d = Date();
      const t = d.slice(0, 3);
      if (t === week[i].day) {
        <Weather
          day={week[i].day}
          low={week[i].low}
          high={week[i].high}
          className={"main"}
        />;

        continue;
      }
      array.push(weather);
    }
    return array;
  };
  return (
    <div className="app">
      {/* <h1 className="header">What's the weather?</h1> */}
      {/* <div className="todayWeather">
        <Weather day="" low="33" high="35" />
      </div> */}
      <div className="divWeather">
        {renderWeek()}
        {/* <Weather day="Sunday" low="33" high="35" />
        <Weather day="Monday" low="26" high="34" />
        <Weather day="Tuesday" low="26" high="34" />
        <Weather day="Wednesday" low="32" high="38" />
        <Weather day="Thursday" low="17" high="21" />
        <Weather day="Friday" low="12" high="14" />
        <Weather day="Saturday" low="-4" high="8" /> */}
      </div>
    </div>
  );
}
export default App;
