import React from "react";
import Weather from "./components/Weather";
import "./App.css";

function App() {
  const week = [
    { day: "Sunday", low: "33", high: "38" },
    { day: "Monday", low: "26", high: "34" },
    { day: "Tue", low: "18", high: "24" },
    { day: "Wednesday", low: "15", high: "20" },
    { day: "Thu", low: "12", high: "14" },
    { day: "Fri", low: "12", high: "14" },
    { day: "Sat", low: "-4", high: "8" },
  ];
  const renderWeek = function () {
    const array = [];
    for (let i = 0; i < week.length; i++) {
      let weather = (
        <Weather day={week[i].day} low={week[i].low} high={week[i].high} />
      );
      // let d = Date();
      // const t = d.slice(0, 3);
      if (myDate() === week[i].day) {
        weather = (
          <Weather
            current={"Today"}
            location={"Rishon Lezion"}
            day={week[i].day}
            low={week[i].low}
            high={week[i].high}
            className={"main"}
          />
        );
      }
      array.push(weather);
    }
    return array;
  };
  return (
    <div className="app">
      <div className="container">
        <h1>WELCOME BACK!</h1>
        {/* <h2 className="location">Location: Rishon Lezion</h2> */}
        {/* <h2 className="location">Choose a day to see the weather</h2> */}
        <h2>Have a nice day!</h2>
      </div>
      <div className="divWeather">{renderWeek()}</div>
    </div>
  );
}
export default App;

function myDate() {
  var a = new Date();
  var weekdays = new Array(7);
  weekdays[0] = "Sunday";
  weekdays[1] = "Monday";
  weekdays[2] = "Tuesday";
  weekdays[3] = "Wednesday";
  weekdays[4] = "Thursday";
  weekdays[5] = "Friday";
  weekdays[6] = "Saturday";
  var r = weekdays[A.getDay()];
  document.getElementById("myId").innerHTML = r;
}
