import React from "react";
import "./Weather.css";
import partlyCloudy from "./WeatherLogo/partlyCloudy.png";
import cloudy from "./WeatherLogo/cloudy.png";
import rainy from "./WeatherLogo/rainy.png";
import storm from "./WeatherLogo/storm.png";
import sunny from "./WeatherLogo/sunny.png";

const findLogo = (min, max) => {
  if (min > 30 && max < 40) {
    //30-40 sunny
    return sunny;
  } else if (min > 25 && max < 35) {
    //25-35 partlyCloudy
    return partlyCloudy;
  } else if (min > 15 && max < 25) {
    //15-25 cloudy
    return cloudy;
  } else if (min > 10 && max < 15) {
    //10-15 rainy
    return rainy;
  } else if (min > -5 && max < 10) {
    //-5-10 storm
    return storm;
  }
};

function addClassByWeather(src) {
  if (src === rainy) {
    return "rain";
  } else if (src === storm) {
    return "storm";
  } else if (src === sunny) {
    return "sun";
  } else if (src === partlyCloudy) {
    return "partlyCloudy";
  } else if (src === cloudy) {
    return "cloudy";
  }
}

function Weather({ day, low, high }) {
  let d = Date();
  const t = d.slice(0, 3);
  if (t === day) {
    console.log("today is " + t);
  }

  return (
    <div className={`weather ${addClassByWeather(findLogo(low, high))}`}>
      <h3 className="header">{day}</h3>
      <img
        className="weatherlogo"
        src={findLogo(low, high)}
        alt="WeatherLogo"
      />
      <div className="highlow">
        <h3 className="low">{low + "째"}</h3>
        <h3 className="high">{high + "째"}</h3>
      </div>
    </div>
  );
}

export default Weather;
